import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

import indexCss from "../CSS/index.module.css"


const Index = () =>{
  // notice that we pass a callback function to initialize the socket
  // we don't need to destructure the 'setSocket' function since we won't be updating the socket state
  const [results, setResults] = useState([])
  const [send, setSend] = useState([])

  const [all, setall] = useState({
    const :[results, setResults] = useState([]),
    const :[send, setSend] = useState([])
  })


  const [socket] = useState(() => io(':8000'));

  console.log(results)


  useEffect(() => {
    // we need to set up all of our event listeners
    // in the useEffect callback function
    console.log('Is this running?');
     socket.on("send_data_to_all_other_clients", data =>{
        setResults(prevResults =>{
            return [data, ...prevResults]

        })
    });
  }, []);

  const submitHandler = (e) => {
    e.preventDefault()

    socket.emit("event_from_client", Object.values(send)[0])
    console.log(Object.values(send))


}

const onChangeHandler = (e) => {
    setSend({
       ...send,
       [e.target.name]: e.target.value
    })


}


  return (
    <div className={indexCss.container}>
      <h1 className={indexCss.messageContainer}>

      {
                        all.map((val, idx) => {
                            return(


                        <div key={idx} className={indexCss.messageBackground}>
                            <h3 className={indexCss.message}>{val}</h3>
                        </div>



                            )
                        })
                      }


      </h1>
      <form id={indexCss.flex} onSubmit={submitHandler}>
          <input type="text" name='send' onChange={onChangeHandler} />
          <button type='submit'>send</button>
      </form>
    </div>
  );
}

export default Index;
