import axios from 'axios'
import io from 'socket.io-client';
import React, { useState, useEffect } from 'react';
import IndexCss from "../CSS/index.module.css"


const Index = () => {

const [socket] = useState(() => io(':8000'));

const [messages, setMessages] = useState(["pee"])

const [errors, setErrors] = useState([]);



useEffect(() => {
    socket.on("new_message_from_server", msg =>{
        console.log("fdsfsdfsfs")

        setMessages(prevMessages =>{

            return [msg, ...prevMessages];


        })

    })

}, []);

const onChangeHandler = (e) => {
    setFormInfo({
       ...messages,
       [e.target.name]: e.target.value
    })


}

const submitHandler = (e) => {
    e.preventDefault()
    axios.post("http://localhost:8000/api/poll/new", messages)
        .then(response => {
            console.log(response)

        })
        .catch(err => {
            const errorResponse = err.response.data.error.errors;
            console.log("This is the catch: " + err.response.data.error.errors )
            const errorArr = [];
            for(const key of Object.keys(errorResponse)){
                errorArr.push(errorResponse[key].message);
            }
            setErrors(errorArr);
            console.log(errorArr)
        })
}


   return (
    <div className="App">
      <div className={IndexCss.container}>
          <div className={IndexCss.messageContainer}>
          {
                        messages.map((mg, idx) => {
                            return(
                        <div key={idx}>
                            <h5>{mg}</h5>
                        </div>

                            )
                        })
                      }

          </div>
          <div className={IndexCss.form}>
              <form>
              <div id={IndexCss.flex} className="mb-3">
                <input type="text" className="form-control" name='messages' onChange={onChangeHandler}/>
                <button type="submit" className={IndexCss.sendBtn}>Send</button>
                </div>

              </form>
          </div>
      </div>
    </div>
  );
}

export default Index
